sp_widget.client_script:
function($scope, spUtil, $rootScope, spModal) {
    /* widget controller */
    var c = this;

     $scope.triggerOnSubmit = function() {
      c.data.buttonClicked = true;
	  c.server.update();
	   window.location.reload();
    }
}

