sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.showButton = false;
	var audJob = new GlideRecord('x_snc_coe_audit_job');
	audJob.get($sp.getParameter("sys_id"));
	if(audJob.state == -1)
	data.showButton = true;

	if(input && input.buttonClicked == true)
	{
		audJob.state = -3;
		audJob.update();
	}


})();
