sp_widget.client_script:
function($scope,$rootScope) {
  /* widget controller */
  var c = this;
	$rootScope.$on('refresh', function(event,data) { 
			//console.log('Listener caught NoteID: ' + JSON.stringify(data)); 
		c.server.update();
		$scope.server.refresh();
		});

	c.server.update();
	$scope.server.refresh();
}

