sp_widget.script:
(function() {
		//console.log('state'+data);
    data.record = $sp.getParameter("sys_id");
    var gr = new GlideRecord('x_snc_coe_audit_job');
    gr.get(data.record);
    gr.query();
    var jobState = gr.state;

	  data.auditor = false;
	  data.hold = false;
	  data.coereview = false;
	  data.progress = false;
	  data.audreview = false;
	  data.close = false;

    if (gr.state == -1)
        data.auditor = true;
    else if (gr.state == -2)
        data.hold = true;
    else if (gr.state == -3)
        data.coereview = true;
    else if (gr.state == -4)
        data.progress = true;
    else if (gr.state == -5)
        data.audreview = true;
    else if (gr.state == 3)
        data.close = true;


})();
