sys_script_include.script:
var OneStopUserSkillAjaxUtil = Class.create();
OneStopUserSkillAjaxUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /**
     * @desc Checks if the user is Accredited or Uncredited.
     * @return String - Returns true if the user is Accredited and false if the user is Uncredited.
     */

    checkUserSkill: function() {

        var user = this.getParameter("sysparm_user");
        var procedure = this.getParameter("sysparm_procedure");

		return new x_snc_coe.OneStopUserSkillUtil().isUserAcredited(user,procedure).toString();
    },

    type: 'OneStopUserSkillAjaxUtil'
});
