sys_script_include.script:
var OneStopPortalFormatter = Class.create();
OneStopPortalFormatter.prototype = {
    initialize: function() {},

    displayFormatter: function(jobClassName, jobType) {

        var formatterProp = JSON.parse(gs.getProperty("x_snc_coe.portal_ui_formatter"));
        for (var i = 0; i < formatterProp.CoEFormatter.length; i++) {

            if (formatterProp.CoEFormatter[i]['className'] == jobClassName && formatterProp.CoEFormatter[i]['jobType'] == jobType) {
                return formatterProp.CoEFormatter[i]["classFormatter"];
            }
        }
        return formatterProp.defaultFormatter;
    },

    readProcessFlowFormatter: function(jobClassName, jobType) {
        var processFlowArray = [];
        var processFlowObj = {};
        var processFlow = new GlideRecord("sys_process_flow");
        processFlow.addQuery("table", jobClassName);
		processFlow.addQuery("label","!=","Cancelled");
		processFlow.orderBy("order");
        processFlow.query();
        while (processFlow.next()) {
            processFlowObj = {
                    "view_condition": processFlow.getValue("u_view_condition"),
                    "condition": processFlow.getValue("condition"),
                    "label": processFlow.getValue("label")
                },
                processFlowArray.push(processFlowObj);
        }
		return processFlowArray;
    },

    type: 'OneStopPortalFormatter'
};
