sys_script_include.script:
var OneStopStreamGroupFilterAJAX = Class.create();
OneStopStreamGroupFilterAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getGroups: function() {
        var groupID = this.getParameter("sysparm_group_id");
        var sgf = new x_snc_coe.OneStopStreamGroupFilter();
        return sgf.getChildGroupsOfSelectedStream(groupID);
    },

    updateGroup: function() {

        var grp = this.getParameter("sysparm_grpid");
        var tbl = this.getParameter("sysparm_table");
        var id = this.getParameter("sysparm_id");
        var sgf2 = new x_snc_coe.OneStopStreamGroupFilter();
        return sgf2.setAssignmentGroup(tbl, id, grp);
    },

    type: 'OneStopStreamGroupFilterAJAX'
});
