sys_script_include.script:
var AuditJobAjaxUtil = Class.create();
AuditJobAjaxUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /*
    @Description : Retrives all varible names associated with a Job Record.
    @param : Sys_id of the Job Record.
    @Return : Array of variable names.
    */

    getAllVaribles: function() {
        var id = this.getParameter("sysparm_id");
        var array = [];
        var gr = new GlideRecord("question_answer");
        gr.addQuery("table_sys_id", id);
        gr.query();
        while (gr.next()) {
            array.push(gr.question.name.toString());
        }
        return JSON.stringify(array);
    },


    type: 'AuditJobAjaxUtil'
});
