sys_script_include.script:
var OneStopColleagueUtils = Class.create();
OneStopColleagueUtils.prototype = {
    initialize: function() {},

    /**
     * @desc Checks if the loggedin to Colleague Profile Table.
     * @return String - Returns list of users with access to the table.
     * @parms : none.
     */
    usersWithAccessToColleagueProfiles: function() {

        var users = [];
        var teamLeadRoles = JSON.parse(gs.getProperty("x_snc_coe.colleague_profile_table_access"));
        var arrLength;
        var grpMember;

        for (var i = 0; i < teamLeadRoles.length; i++) {
            arrLength = teamLeadRoles[i].role.length;
            if (arrLength > 0) {
                for (var j = 0; j < arrLength; j++) {
                    if (gs.hasRole(teamLeadRoles[i].role[j])) {
                        grpMember = new GlideRecord("sys_user_grmember");
                        grpMember.addQuery(teamLeadRoles[i].filter);
                        grpMember.query();
                        while (grpMember.next()) {
                            users.push(grpMember.getValue("user"));
                        }
                    }
                }
            }

        }
		gs.info("Testing Script "+users.length);
        return users.toString();
    },

    type: 'OneStopColleagueUtils'
};
