sys_script_include.script:
var OneStopUserSkillUtil = Class.create();
OneStopUserSkillUtil.prototype = {
    initialize: function() {},

    /*
	* @Description: Checks if user has Accredited Skill based on procedure.
	* @param: (sys_user SysID, procedure String)
	* @return: (Boolean)
	*/
	isUserAcredited: function(userID, procedure) {	

		var skillName =[procedure+" L2 Accredited",procedure+" L3 Accredited" ].join(",");
		var usk = new GlideAggregate("sys_user_has_skill");
        usk.addQuery("user", userID);
		usk.addQuery("skill.nameIN"+skillName);
        usk.addAggregate("COUNT");
        usk.query();
        usk.next();
		return usk.getAggregate("COUNT") > 0;
},

    type: 'OneStopUserSkillUtil'
};
