sys_script_include.script:
var OneStopGroups = Class.create();
OneStopGroups.prototype = {
    initialize: function() {},

    getGroups: function(jobType) {
        var grpProp = JSON.parse(gs.getProperty("x_snc_coe.clara_group_selection"));
        var sysidArr = [];
        for (var i in grpProp) {
            if (grpProp[i].jobType == jobType && jobType == "aos") {
                var grp = new GlideRecord("sys_user_group");
                grp.addQuery("sys_idIN" + grpProp[i].streamGroups);
                grp.query();
                while (grp.next()) {
                    sysidArr.push(grp.getUniqueValue());
                }
            }
			if(jobType == "clara"){
				var grp2 = new GlideRecord("sys_user_group");
				grp2.addQuery("parent","9d30957f1bf36c10d1807b46464bcbf2");
				grp2.query();
				while(grp2.next()){
					sysidArr.push(grp2.getUniqueValue());
				}
			}
        }
        return "sys_idIN" + sysidArr.toString();
    },

	getAssignmentGroups : function(groupID){
		var grpArr = [];
		var group = new GlideRecord("sys_user_group");
		group.addQuery("parent",groupID);
		group.query();
		while(group.next()){
			grpArr.push(group.getUniqueValue());
		}
		return "sys_idIN"+grpArr.toString();
	},

    type: 'OneStopGroups'
};
