sys_script_include.script:
var AuditSecurityUtils = Class.create();
AuditSecurityUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getIndependenceCheck: function() {
        var flag = false;
        var id = this.getParameter("sysparm_sys_id");
        var arrVal = [];
        var indc = new GlideRecord("x_snc_coe_independence_check");
        indc.addQuery("audit_job", id);
        indc.query();
        while (indc.next()) {
            if (indc.independent == "no") {
                flag = true;
            }
        }
        if (flag == false) {
            var job = new GlideRecord('x_snc_coe_audit_job');
            job.get(id);
            job.state = -3;
            job.update();
        }

        return flag;
    },

    type: 'AuditSecurityUtils'
});
