sys_script_include.script:
var AuditTaskUtils = Class.create();
AuditTaskUtils.prototype = {
    initialize: function() {},

    /*
     * @Description: Maps Audit Task task status with state. For any given Status, returns an
     * an equivalent status
     * @param (String) Status value
     * @return (String) State value
     */
    getStateFromStatusMap: function(statusParam) {

        if (!statusParam) {
            gs.info("x_snc_coe.AuditTaskUtils.statusToStateMapping(statusParam) statusParam must be a String.");
            return;
        }

        var state = AuditGlobals.taskState,
            status = AuditGlobals.taskStatus,
            rtnParam;

        switch (statusParam) {
            case status.PENDING_ASSIGNMENT:
                rtnParam = state.PENDING_ASSIGNMENT;
                break;
            case status.BEING_EXECUTED:
                rtnParam = state.BEING_EXECUTED;
                break;
            case status.COE_REVIEW:
                rtnParam = state.COE_REVIEW;
                break;
            case status.AUDITOR_REVIEW:
                rtnParam = state.AUDITOR_REVIEW;
                break;
            case status.SCHEDULED:
                rtnParam = state.SCHEDULED;
                break;
            case status.AWAITING_PBM:
                rtnParam = state.AWAITING_PBM;
                break;
            case status.AUDITOR_QUERY:
                rtnParam = state.PEND_ENG_TEAM_QUERY;
                break;
            case status.QC_REVIEW:
                rtnParam = state.CLOSED;
                break;
            case status.QC_CLOSED:
                rtnParam = state.CLOSED;
                break;
            case status.AUDITOR_INPUT:
                rtnParam = state.AUDITOR_INPUT;
                break;
            case status.CLOSED:
                rtnParam = state.CLOSED;
                break;
            case status.CANCELLED:
                rtnParam = state.CANCELLED;
                break;
            case status.AWAITING_AUDITOR_REVIEW_MSD:
                rtnParam = state.AWAITING_AUDITOR_REVIEW_MSD;
                break;
            case status.AWAITING_BUSINESS_APPROVAL_MSD:
                rtnParam = state.AWAITING_BUSINESS_APPROVAL_MSD;
                break;
            case status.PEND_ENG_TEAM_QUERY:
                rtnParam = state.PEND_ENG_TEAM_QUERY;
                break;
            case status.AWAITING_APPROVAL_IN_MSD:
                rtnParam = state.AWAITING_APPROVAL_IN_MSD;
                break;
            default:
                rtnParam = undefined;
        }

        return rtnParam;
    },

    /*
     * @Description: If the class name passed is one of Audit CoEs it will return true
     * @Param: (String) sys_class_name of the task record
     * @return: (Boolean) 
     */
    isClassPartOfOnboardedCoeTasks: function(className) {

        var auditCoeTables = gs.getProperty("x_snc_coe.onboarded_coe_task_class_names").split(",");
        for (var i in auditCoeTables) {
            if (auditCoeTables[i] == className) {
                return true;
            }
        }
        return false;
    },






    type: 'AuditTaskUtils'
};
